mapper.select:
SELECT



FROM nongzi  a



        and a.id in

        #{item}



        and a.nongzi_name like CONCAT('%',#{params.nongziName},'%')


        and a.nongzi_uuid_number like CONCAT('%',#{params.nongziUuidNumber},'%')


        and a.nongzi_types = #{params.nongziTypes}


          and a.nongzi_kucun_number >= #{params.nongziKucunNumberStart}   


          and a.nongzi_kucun_number <= #{params.nongziKucunNumberEnd}   


        and a.nongzi_kucun_number = #{params.nongziKucunNumber}


          and a.nongzi_new_money >= #{params.nongziNewMoneyStart}   


          and a.nongzi_new_money <= #{params.nongziNewMoneyEnd}   


        and a.nongzi_new_money = #{params.nongziNewMoney}


          and a.nongzi_clicknum >= #{params.nongziClicknumStart}   


          and a.nongzi_clicknum <= #{params.nongziClicknumEnd}   


        and a.nongzi_clicknum = #{params.nongziClicknum}


        and a.nongzi_content like CONCAT('%',#{params.nongziContent},'%')


          and a.nongzi_delete >= #{params.nongziDeleteStart}   


          and a.nongzi_delete <= #{params.nongziDeleteEnd}   


        and a.nongzi_delete = #{params.nongziDelete}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}



order by a.${params.sort} ${params.order}
